function playbtu() {
  var oyd = document.getElementById("ydmc");
  if (window.yaudio.paused) {
    window.yaudio.play();
    window.disableautoplay = true;
    oyd.className = "iconfont icon-music icon-spin-music";
  } else {
    window.yaudio.pause();
    oyd.className = "iconfont icon-player";
  }
}
function next() {
  var oyd = document.getElementById("ydmc");
  if (window.musicIndex == window.musicArr.length - 1) {
    window.musicIndex = 0;
  } else {
    window.musicIndex = window.musicIndex + 1;
  }
  window.currentMusic = window.musicArr[window.musicIndex];
  yaudio.src = window.currentMusic.mp3;
  yaudio.ti = window.currentMusic.title;
  yaudio.art = window.currentMusic.artist;
  window.yaudio.play();
  window.disableautoplay = true;
  oyd.className = "iconfont icon-music icon-spin-music";
}

yaudio.addEventListener(
  "ended",
  function () {
    next();
  },
  false
);
